import jxl.*
def filepath = "C://Users//user//Desktop//Test Data Soapui Assignment.xls"
//where the file is to read
File Excel = new File(filepath)
//i have to get the workbook
Workbook wb = Workbook.getWorkbook(Excel)
//i am trying to get the sheet
Sheet sh = wb.getSheet("Sheet1")
def row = sh.getRows().toInteger()
def column = sh.getColumns().toInteger()
log.info "The number of rows are $row and The Number of column are $column"

//i want to fetch the data in each row and column
for(def i=0;i<row;i++)
{
	for(def j=0;j<column;j++)
	{
		if(j==0)
		{
			email = sh.getCell(j,i).getContents()
		}
		else if(j==1)
		{
			firstname = sh.getCell(j,i).getContents()
		}
		else
		{
			lastname = sh.getCell(j,i).getContents()
		}
	}


	def tc = testRunner.testCase.testSuite.project.testSuites["Functional Testing"].testCases["Property Expansion"]

tc.testSteps["properties"].setPropertyValue("email",email)
tc.testSteps["properties"].setPropertyValue("firstname",firstname)
tc.testSteps["properties"].setPropertyValue("lastname",lastname)
//this line will execute the entire testcase
def runner = tc.run(null,false)
def teststeplist = tc.getTestStepList()
//log.info TSL
//i want see the names of the my test steps
teststeplist.each
{
	def tsname = it.name
	log.info tsname
	//fetch the currect step which i am executing
	//log.info tc.getCurrentStep().name
}
